function getChordNames(options) {
  let chordNames = [];

  if (options["major"]) {
    chordNames.push(...matchChordTypeToChordNames["major"]);
  }
  if (options["minor"]) {
    chordNames.push(...matchChordTypeToChordNames["minor"]);
  }
  if (options["7"]) {
    chordNames.push(...matchChordTypeToChordNames["7"]);
  }
  if (options["m7"]) {
    chordNames.push(...matchChordTypeToChordNames["m7"]);
  }
  if (options["M7"]) {
    chordNames.push(...matchChordTypeToChordNames["M7"]);
  }
  if (options["mM7"]) {
    chordNames.push(...matchChordTypeToChordNames["mM7"]);
  }

  console.log("from getChordNames: ", chordNames);

  return chordNames;
}

